    id_cp: cp.id_cp,
    id_mapel: cp.id_mapel,
    fase: cp.fase,
    deskripsi_cp: cp.deskripsi_cp,
    nama_mapel: cp.nama_mapel
  });
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setMessageType('');
    try {
      // Kirim hanya deskripsi_cp
      const response = await adminApi.updateCapaianPembelajaran(editedCp.id_cp, {
        deskripsi_cp: editedCp.deskripsi_cp
      });
      setMessage(response.message);
      setMessageType('success');
      onSave(); // Refresh daftar
      onClose(); // Tutup modal setelah simpan
    } catch (err) {
      console.error('Error updating CP:', err);
      setMessage(err.message);
      setMessageType('error');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>Edit Capaian Pembelajaran</h3>
        <div style={{ marginBottom: '1rem' }}>
          <strong>Mata Pelajaran:</strong> {cp.nama_mapel}<br/>
          <strong>Fase:</strong> {cp.fase}
        </div>
        {message && <div className={`message ${messageType}`}>{message}</div>}
        <form onSubmit={handleSubmit} className="form-container-small">
          <div className="form-group">
            <label>Deskripsi CP:</label>
            <textarea
              name="deskripsi_cp"
              value={editedCp.deskripsi_cp}
              onChange={(e) => setEditedCp(prev => ({ ...prev, deskripsi_cp: e.target.value }))}
              required
              style={{ height: '150px' }}
              autoFocus
            ></textarea>
          </div>
          <div className="modal-actions">
            <button type="submit" className="submit-button">Simpan Perubahan</button>
            <button type="button" onClick={onClose} className="cancel-button">Batal</button>
          </div>
        </form>
      </div>
    </div>
  );
};


// Komponen Import Excel
const ImportExcel = ({ onImportSuccess }) => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setMessage('Pilih file Excel terlebih dahulu');
      setMessageType('error');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/excel/import-cp', {
        method: 'POST',
        body: formData,
      });

      let data;
      try {
        data = await response.json();
      } catch (err) {
        throw new Error('Gagal memproses response dari server. Pastikan format file Excel sesuai.');
      }

      if (!response.ok) throw new Error(data.message || 'Gagal mengimport file');

      setMessage(data.message);
      setMessageType('success');
      onImportSuccess(); // Refresh data
      setFile(null);
      // Reset file input
      e.target.reset();
    } catch (err) {
      setMessage(err.message);
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="import-section" style={{ marginBottom: '2rem' }}>
      <h4>Import Data dari Excel</h4>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="file"
            accept=".xlsx,.xls"
            onChange={handleFileChange}
            className="file-input"
          />
          <button 
            type="submit" 
            className="submit-button"
            disabled={loading}
            style={{ marginLeft: '1rem' }}
          >
            {loading ? 'Mengimport...' : 'Import Excel'}
          </button>
        </div>
        {message && (
          <div className={`message ${messageType}`}>
            {message}
          </div>
        )}
      </form>
    </div>
  );
};

const CapaianPembelajaranManagement = () => {
  const [cps, setCps] = useState([]);
  const [mataPelajaranOptions, setMataPelajaranOptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newCp, setNewCp] = useState({
    id_mapel: '',
    fase: 'A',
    deskripsi_cp: ''
  });
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedCp, setSelectedCp] = useState(null);

  const fetchCpsAndMapel = async () => {
    setLoading(true);
    setError(null);
    try {
      const [cpsData, mapelData] = await Promise.all([
        adminApi.getCapaianPembelajaran(),
        adminApi.getMataPelajaran()
      ]);
      setCps(cpsData);
      setMataPelajaranOptions(mapelData);
      if (mapelData.length > 0 && !newCp.id_mapel) {
        setNewCp(prev => ({ ...prev, id_mapel: mapelData[0].id_mapel }));
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCpsAndMapel();
  }, []);

  const handleAddCp = async (e) => {
    e.preventDefault();
    setMessage('');
    setMessageType('');
    try {
      const response = await adminApi.addCapaianPembelajaran({
        id_mapel: parseInt(newCp.id_mapel), // Pastikan integer
        fase: newCp.fase,
        deskripsi_cp: newCp.deskripsi_cp
      });
      setMessage(response.message);
      setMessageType('success');
      setNewCp({
        id_mapel: mataPelajaranOptions.length > 0 ? mataPelajaranOptions[0].id_mapel : '',
        fase: 'A',
        deskripsi_cp: ''
      });
      fetchCpsAndMapel(); // Refresh daftar
    } catch (err) {
      setMessage(err.message);
      setMessageType('error');
    }
  };

  const handleEditClick = (cp) => {
    setSelectedCp(cp);
    setShowEditModal(true);
  };

  const handleDeleteClick = async (id_cp, deskripsi_cp) => {
    if (window.confirm(`Apakah Anda yakin ingin menghapus Capaian Pembelajaran: "${deskripsi_cp}" (ID: ${id_cp})? Tindakan ini tidak dapat dibatalkan.`)) {
      setMessage('');
      setMessageType('');
      try {
        const response = await adminApi.deleteCapaianPembelajaran(id_cp);
        setMessage(response.message);
        setMessageType('success');
        fetchCpsAndMapel(); // Refresh daftar
      } catch (err) {
        setMessage(err.message);
        setMessageType('error');
      }
    }
  };

  if (loading) return <p>Memuat Capaian Pembelajaran...</p>;
  if (error) return <p className="message error">Error: {error}</p>;

  return (
    <div className="feature-content">
      <h2>Manajemen Capaian Pembelajaran</h2>
      {message && <div className={`message ${messageType}`}>{message}</div>}
      <ImportExcel onImportSuccess={fetchCpsAndMapel} />

      <h4>Tambah Capaian Pembelajaran Baru</h4>
      <form onSubmit={handleAddCp} className="form-container-small">
        <div className="form-group">
          <label>Mata Pelajaran:</label>
          <select
            name="id_mapel"
            value={newCp.id_mapel}
            onChange={(e) => setNewCp({ ...newCp, id_mapel: e.target.value })}
            required
          >
            <option value="">Pilih Mata Pelajaran</option>
            {mataPelajaranOptions.map(mapel => (
              <option key={mapel.id_mapel} value={mapel.id_mapel}>{mapel.nama_mapel}</option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>Fase:</label>
          <select
            name="fase"
            value={newCp.fase}
            onChange={(e) => setNewCp({ ...newCp, fase: e.target.value })}
            required
          >
            <option value="A">Fase A</option>
            <option value="B">Fase B</option>
            <option value="C">Fase C</option>
          </select>
        </div>
        <div className="form-group">
          <label>Deskripsi CP:</label>
          <textarea
            name="deskripsi_cp"
            value={newCp.deskripsi_cp}
            onChange={(e) => setNewCp({ ...newCp, deskripsi_cp: e.target.value })}
            required
          ></textarea>
        </div>
        <button type="submit" className="submit-button">Tambah Capaian Pembelajaran</button>
      </form>

      <h4>Daftar Capaian Pembelajaran</h4>
      {cps.length > 0 ? (
        <div>
          {mataPelajaranOptions.map(mapel => {
            // Filter CP untuk mata pelajaran ini
            const cpMapel = cps.filter(cp => cp.nama_mapel === mapel.nama_mapel);
            if (cpMapel.length === 0) return null; // Skip jika tidak ada CP

            return (
              <div key={mapel.id_mapel} style={{ marginBottom: '2rem' }}>
                <h5 style={{ marginBottom: '1rem', color: '#2c3e50' }}>{mapel.nama_mapel}</h5>
                <table className="data-table">
                  <thead>
                    <tr>
                      <th style={{ width: '10%' }}>Fase</th>
                      <th style={{ width: '70%' }}>Deskripsi CP</th>
                      <th style={{ width: '20%' }}>Aksi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {['A', 'B', 'C'].map(fase => {
                      const cpFase = cpMapel.find(cp => cp.fase === fase);
                      if (!cpFase) {
                        return (
                          <tr key={`${mapel.id_mapel}-${fase}`} style={{ backgroundColor: '#f8f9fa' }}>
                            <td>{fase}</td>
                            <td colSpan="2" style={{ textAlign: 'center', color: '#6c757d' }}>
                              Belum ada CP untuk fase ini
                            </td>
                          </tr>
                        );
                      }
                      return (
                        <tr key={cpFase.id_cp}>
                          <td>{cpFase.fase}</td>
                          <td>{cpFase.deskripsi_cp}</td>
                          <td>
                            <button 
                              onClick={() => handleEditClick(cpFase)} 
                              className="action-button edit-button"
                              style={{ marginRight: '0.5rem' }}
                            >
                              Edit
                            </button>
                            <button 
                              onClick={() => handleDeleteClick(cpFase.id_cp, cpFase.deskripsi_cp)} 
                              className="action-button delete-button"
                            >
                              Hapus
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            );
          })}
        </div>
      ) : (
        <p>Belum ada Capaian Pembelajaran yang terdaftar.</p>
      )}

      {showEditModal && selectedCp && (
        <EditCapaianPembelajaranModal
          cp={selectedCp}
          onClose={() => setShowEditModal(false)}
          onSave={fetchCpsAndMapel}
        />
      )}
    </div>
  );
};

export default CapaianPembelajaranManagement;
