// frontend/src/features/admin/CapaianPembelajaranManagement.js
import React, { useState, useEffect } from 'react';
import * as adminApi from '../../api/admin';

// Komponen Modal Edit Capaian Pembelajaran
const EditCapaianPembelajaranModal = ({ cp, onClose, onSave }) => {
  // Pastikan semua field yang diperlukan tersedia saat inisialisasi
  const [editedCp, setEditedCp] = useState({
    id_cp: cp.id_cp,
    id_mapel: cp.id_mapel,
    fase: cp.fase,
    deskripsi_cp: cp.deskripsi_cp,
    nama_mapel: cp.nama_mapel
  });
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setMessageType('');
    try {
      // Kirim hanya deskripsi_cp
      const response = await adminApi.updateCapaianPembelajaran(editedCp.id_cp, {
        deskripsi_cp: editedCp.deskripsi_cp
      });
      setMessage(response.message);
      setMessageType('success');
      onSave(); // Refresh daftar
      onClose(); // Tutup modal setelah simpan
    } catch (err) {
      console.error('Error updating CP:', err);
      setMessage(err.message);
      setMessageType('error');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>Edit Capaian Pembelajaran</h3>
        <div className="form-group">
          <strong>Mata Pelajaran:</strong> {cp.nama_mapel}<br/>
          <strong>Fase:</strong> {cp.fase}
        </div>
        {message && <div className={`message ${messageType}`}>{message}</div>}
        <form onSubmit={handleSubmit} className="form-container-small">
          <div className="form-group">
            <label>Deskripsi CP:</label>
            <textarea
              name="deskripsi_cp"
              value={editedCp.deskripsi_cp}
              onChange={(e) => setEditedCp(prev => ({ ...prev, deskripsi_cp: e.target.value }))}
              required
              className="textarea-large"
              autoFocus
            ></textarea>
          </div>
          <div className="modal-actions">
            <button type="submit" className="submit-button">Simpan Perubahan</button>
            <button type="button" onClick={onClose} className="cancel-button">Batal</button>
          </div>
        </form>
      </div>
    </div>
  );
};


// Komponen Import Excel
const ImportExcel = ({ onImportSuccess }) => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setMessage('Pilih file Excel terlebih dahulu');
      setMessageType('error');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/excel/import-cp', {
        method: 'POST',
        body: formData,
      });

      let data;
      try {
        data = await response.json();
      } catch (err) {
        throw new Error('Gagal memproses response dari server. Pastikan format file Excel sesuai.');
      }

      if (!response.ok) throw new Error(data.message || 'Gagal mengimport file');

      setMessage(data.message);
      setMessageType('success');
      onImportSuccess(); // Refresh data
      setFile(null);
      // Reset file input
      e.target.reset();
    } catch (err) {
      setMessage(err.message);
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="import-section" style={{ marginBottom: '2rem' }}>
      <h4>Import Data dari Excel</h4>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="file"
            accept=".xlsx,.xls"
            onChange={handleFileChange}
            className="file-input"
          />
          <button 
            type="submit" 
            className="submit-button"
            disabled={loading}
            style={{ marginLeft: '1rem' }}
          >
            {loading ? 'Mengimport...' : 'Import Excel'}
          </button>
        </div>
        {message && (
          <div className={`message ${messageType}`}>
            {message}
          </div>
        )}
      </form>
    </div>
  );
};

const CapaianPembelajaranManagement = () => {
  const [cps, setCps] = useState([]);
  const [mataPelajaranOptions, setMataPelajaranOptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newCp, setNewCp] = useState({
    id_mapel: '',
    fase: 'A',
    deskripsi_cp: ''
  });
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedCp, setSelectedCp] = useState(null);

  const fetchCpsAndMapel = async () => {
    setLoading(true);
    setError(null);
    try {
      const [cpsData, mapelData] = await Promise.all([
        adminApi.getCapaianPembelajaran(),
        adminApi.getMataPelajaran()
      ]);
      setCps(cpsData);
      setMataPelajaranOptions(mapelData);
      if (mapelData.length > 0 && !newCp.id_mapel) {
        setNewCp(prev => ({ ...prev, id_mapel: mapelData[0].id_mapel }));
      }
    } catch (err) {
      console.error('Error fetching data:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCpsAndMapel();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    // Delay feather.replace() to ensure DOM is fully rendered
    const timeoutId = setTimeout(() => {
      try {
        feather.replace();
      } catch (error) {
        console.warn('Feather icons replace error:', error);
      }
    }, 0);

    return () => clearTimeout(timeoutId);
  }, [cps, mataPelajaranOptions]);

  const handleAddCp = async (e) => {
    e.preventDefault();
    setMessage('');
    setMessageType('');
    try {
      const response = await adminApi.addCapaianPembelajaran({
        id_mapel: parseInt(newCp.id_mapel), // Pastikan integer
        fase: newCp.fase,
        deskripsi_cp: newCp.deskripsi_cp
      });
      setMessage(response.message);
      setMessageType('success');
      setNewCp({
        id_mapel: mataPelajaranOptions.length > 0 ? mataPelajaranOptions[0].id_mapel : '',
        fase: 'A',
        deskripsi_cp: ''
      });
      fetchCpsAndMapel(); // Refresh daftar
    } catch (err) {
      setMessage(err.message);
      setMessageType('error');
    }
  };

  const handleEditClick = (cp) => {
    setSelectedCp(cp);
    setShowEditModal(true);
  };

  const handleDeleteClick = async (id_cp, deskripsi_cp) => {
    if (window.confirm(`Apakah Anda yakin ingin menghapus Capaian Pembelajaran: "${deskripsi_cp}" (ID: ${id_cp})? Tindakan ini tidak dapat dibatalkan.`)) {
      setMessage('');
      setMessageType('');
      try {
        const response = await adminApi.deleteCapaianPembelajaran(id_cp);
        setMessage(response.message);
        setMessageType('success');
        fetchCpsAndMapel(); // Refresh daftar
      } catch (err) {
        setMessage(err.message);
        setMessageType('error');
      }
    }
  };

  return (
    <div className="feature-content">
      <div className="dashboard-header-main" style={{ marginBottom: '20px' }}>
        <h2>Manajemen Capaian Pembelajaran</h2>
        <p>Kelola capaian pembelajaran untuk setiap mata pelajaran</p>
      </div>

      {message && <div className={`message ${messageType}`} style={{ marginBottom: '20px' }}>{message}</div>}

      {/* Import Section with Card Design */}
      <div className="dashboard-feature-content" style={{ marginBottom: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}>
          <i data-feather="upload" style={{ marginRight: '10px', color: '#007bff' }}></i>
          <h4 style={{ margin: 0, color: '#333' }}>Import Data dari Excel</h4>
        </div>
        <ImportExcel onImportSuccess={fetchCpsAndMapel} />
      </div>

      {/* Add New CP Section with Card Design */}
      <div className="dashboard-feature-content" style={{ marginBottom: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}>
          <i data-feather="plus-circle" style={{ marginRight: '10px', color: '#28a745' }}></i>
          <h4 style={{ margin: 0, color: '#333' }}>Tambah Capaian Pembelajaran Baru</h4>
        </div>
        <form onSubmit={handleAddCp} className="form-container-small">
          <div className="form-row">
            <div className="form-column">
              <div className="form-group">
                <label>Mata Pelajaran:</label>
                <select
                  name="id_mapel"
                  value={newCp.id_mapel}
                  onChange={(e) => setNewCp({ ...newCp, id_mapel: e.target.value })}
                  required
                >
                  <option value="">Pilih Mata Pelajaran</option>
                  {mataPelajaranOptions.map(mapel => (
                    <option key={mapel.id_mapel} value={mapel.id_mapel}>{mapel.nama_mapel}</option>
                  ))}
                </select>
              </div>
            </div>
            <div className="form-column">
              <div className="form-group">
                <label>Fase:</label>
                <select
                  name="fase"
                  value={newCp.fase}
                  onChange={(e) => setNewCp({ ...newCp, fase: e.target.value })}
                  required
                >
                  <option value="A">Fase A</option>
                  <option value="B">Fase B</option>
                  <option value="C">Fase C</option>
                </select>
              </div>
            </div>
          </div>
          <div className="form-group">
            <label>Deskripsi CP:</label>
            <textarea
              name="deskripsi_cp"
              value={newCp.deskripsi_cp}
              onChange={(e) => setNewCp({ ...newCp, deskripsi_cp: e.target.value })}
              required
              placeholder="Jelaskan capaian pembelajaran yang harus dicapai siswa..."
              style={{ minHeight: '80px' }}
            ></textarea>
          </div>
          <div style={{ textAlign: 'right' }}>
            <button type="submit" className="submit-button" style={{ width: 'auto', padding: '10px 30px' }}>
              <i data-feather="plus" style={{ marginRight: '8px' }}></i>
              Tambah Capaian Pembelajaran
            </button>
          </div>
        </form>
      </div>

      {/* List Section with Card Design */}
      <div className="dashboard-feature-content">
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}>
          <i data-feather="book-open" style={{ marginRight: '10px', color: '#6f42c1' }}></i>
          <h4 style={{ margin: 0, color: '#333' }}>Daftar Capaian Pembelajaran</h4>
        </div>

        {cps.length > 0 ? (
          <div>
            {mataPelajaranOptions
              .filter(mapel => cps.some(cp => cp.id_mapel === mapel.id_mapel))
              .map(mapel => {
                // Filter CP untuk mata pelajaran ini berdasarkan id_mapel
                const cpMapel = cps.filter(cp => cp.id_mapel === mapel.id_mapel);

                return (
                  <div key={mapel.id_mapel} className="cp-mapel-card">
                    <div className="cp-section-header">
                      <i data-feather="folder"></i>
                      <h5>{mapel.nama_mapel}</h5>
                    </div>

                    <div className="grades-table-wrapper">
                      <table className="data-table">
                        <thead>
                          <tr>
                            <th style={{ width: '15%', textAlign: 'center' }}>Fase</th>
                            <th style={{ width: '70%' }}>Deskripsi Capaian Pembelajaran</th>
                            <th style={{ width: '15%', textAlign: 'center' }}>Aksi</th>
                          </tr>
                        </thead>
                        <tbody>
                          {['A', 'B', 'C'].map(fase => {
                            const cpFase = cpMapel.find(cp => cp.fase === fase);
                            if (!cpFase) {
                              return (
                                <tr key={`${mapel.id_mapel}-${fase}`} className="empty-row">
                                  <td style={{ textAlign: 'center', fontWeight: 'bold' }}>{fase}</td>
                                  <td colSpan="2" style={{
                                    textAlign: 'center',
                                    color: '#6c757d',
                                    fontStyle: 'italic',
                                    padding: '20px'
                                  }}>
                                    <i data-feather="file-x" style={{ marginRight: '8px', color: '#6c757d' }}></i>
                                    Belum ada capaian pembelajaran untuk fase ini
                                  </td>
                                </tr>
                              );
                            }
                            return (
                              <tr key={cpFase.id_cp}>
                                <td style={{ textAlign: 'center', fontWeight: 'bold', fontSize: '16px' }}>
                                  <span className={`cp-fase-badge fase-${cpFase.fase.toLowerCase()}`}>
                                    Fase {cpFase.fase}
                                  </span>
                                </td>
                                <td style={{ padding: '15px' }}>{cpFase.deskripsi_cp}</td>
                                <td style={{ textAlign: 'center' }}>
                                  <div style={{ display: 'flex', gap: '5px', justifyContent: 'center' }}>
                                    <button
                                      onClick={() => handleEditClick(cpFase)}
                                      className="action-button edit-button"
                                      style={{ fontSize: '12px', padding: '6px 12px' }}
                                      title="Edit Capaian Pembelajaran"
                                    >
                                      <i data-feather="edit-2" style={{ width: '14px', height: '14px' }}></i>
                                    </button>
                                    <button
                                      onClick={() => handleDeleteClick(cpFase.id_cp, cpFase.deskripsi_cp)}
                                      className="action-button delete-button"
                                      style={{ fontSize: '12px', padding: '6px 12px' }}
                                      title="Hapus Capaian Pembelajaran"
                                    >
                                      <i data-feather="trash-2" style={{ width: '14px', height: '14px' }}></i>
                                    </button>
                                  </div>
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  </div>
                );
              })}
          </div>
        ) : (
          <div className="cp-empty-state">
            <i data-feather="book"></i>
            <p style={{ fontSize: '16px', margin: 0 }}>Belum ada Capaian Pembelajaran yang terdaftar.</p>
            <p style={{ fontSize: '14px', margin: '5px 0 0 0', color: '#adb5bd' }}>
              Tambahkan capaian pembelajaran baru atau import dari file Excel.
            </p>
          </div>
        )}
      </div>

      {showEditModal && selectedCp && (
        <EditCapaianPembelajaranModal
          cp={selectedCp}
          onClose={() => setShowEditModal(false)}
          onSave={fetchCpsAndMapel}
        />
      )}
    </div>
  );
};

export default CapaianPembelajaranManagement;
